mapper.select:
select * from t_parking_car_record where  entry_time = #{entryTime}   and company_id = #{companyId}

    and car_no = #{carNo}


    and card_no = #{cardNo}

SELECT COALESCE(SUM(pay_money),0)
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and pay_time >= #{before,jdbcType=INTEGER}


    and pay_time < #{after,jdbcType=INTEGER}

SELECT COUNT(id)
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and entry_time >= #{before,jdbcType=INTEGER}


    and entry_time < #{after,jdbcType=INTEGER}

SELECT COUNT(id)
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and exit_time >= #{before,jdbcType=INTEGER}


    and exit_time < #{after,jdbcType=INTEGER}

        SELECT exit_pass_type,COALESCE(SUM(exit_pass_type),0) as sum
        FROM t_parking_car_record
        where 1=1

            and company_id = #{companyId,jdbcType=VARCHAR}


            and park_id = #{parkId,jdbcType=VARCHAR}


            and exit_time >= #{before,jdbcType=INTEGER}


            and exit_time < #{after,jdbcType=INTEGER}

	    GROUP BY exit_pass_type

        SELECT entry_pass_type,COALESCE(SUM(entry_pass_type),0) as sum
        FROM t_parking_car_record
        where 1=1

            and company_id = #{companyId,jdbcType=VARCHAR}


            and park_id = #{parkId,jdbcType=VARCHAR}


            and entry_time >= #{before,jdbcType=INTEGER}


            and entry_time < #{after,jdbcType=INTEGER}

	    GROUP BY entry_pass_type

SELECT use_space
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and entry_time >= #{before,jdbcType=INTEGER}


    and entry_time < #{after,jdbcType=INTEGER}

order by entry_time desc limit 1

SELECT total_space
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and entry_time >= #{before,jdbcType=INTEGER}


    and entry_time < #{after,jdbcType=INTEGER}

order by entry_time desc limit 1
