mapper.select:
select * from t_parking_car_record where  entry_time = #{entryTime}   and company_id = #{companyId}

    and car_no = #{carNo}


    and card_no = #{cardNo}

SELECT COUNT(id)
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and entry_time >= #{before,jdbcType=INTEGER}


    and entry_time < #{after,jdbcType=INTEGER}

SELECT COUNT(id)
FROM t_parking_car_record
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and exit_time >= #{before,jdbcType=INTEGER}


    and exit_time < #{after,jdbcType=INTEGER}

        SELECT exit_pass_type,COUNT(exit_pass_type)
        FROM t_parking_car_record
        where 1=1

            and company_id = #{companyId,jdbcType=VARCHAR}


            and park_id = #{parkId,jdbcType=VARCHAR}


            and exit_time >= #{before,jdbcType=INTEGER}


            and exit_time < #{after,jdbcType=INTEGER}

	    GROUP BY exit_pass_type

        SELECT entry_pass_type,COUNT(entry_pass_type)
        FROM t_parking_car_record
        where 1=1

            and company_id = #{companyId,jdbcType=VARCHAR}


            and park_id = #{parkId,jdbcType=VARCHAR}


            and entry_time >= #{before,jdbcType=INTEGER}


            and entry_time < #{after,jdbcType=INTEGER}

	    GROUP BY entry_pass_type
