mapper.select:
SELECT count(amount)
FROM t_parking_recharge_info_post
where 1=1

and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and create_time >= #{before,jdbcType=INTEGER}


    and create_time < #{after,jdbcType=INTEGER}

SELECT create_time,amount
FROM t_parking_recharge_info_post
where 1=1

    and company_id = #{companyId,jdbcType=VARCHAR}


    and park_id = #{parkId,jdbcType=VARCHAR}


    and create_time >= #{before,jdbcType=INTEGER}


    and create_time < #{after,jdbcType=INTEGER}
